var markers=[
    {
        lat:"-8.853818",
        lng:"13.241412",
        description:"Grupo JK"
    }];

    window.onload=function(){
        var e= {
            center: new google.maps.LatLng(markers[0].lat,markers[0].lng),
            zoom:10,
            flat:!0,
            scrollwheel:!1,
            panControl:!1,
            zoomControl:!0,
            streetViewControl:!1,
            mapTypeControl:!1,
            mapTypeId:google.maps.MapTypeId.ROADMAP},
            l=new google.maps.InfoWindow,
            t=new google.maps.Map(document.getElementById("map"),e);
            for(i=0;i<markers.length;i++){var r=markers[i],a=new google.maps.LatLng(r.lat,r.lng);!function(e,r){
                google.maps.event.addListener(e,"click",function(a){l.setContent(r.description),l.open(t,e)})
            }(new google.maps.Marker({position:a,map:t,icon:"/map.png",title:r.title}),r)
        }
    };